responses.body:
[
  {{setVar 'random' (oneOf (array 1 2 3))}}
  {{#if (urlParam 'code')}}
  {{# repeat (faker 'datatype.number' min=1 max=4) }}
  {
    "id": "{{ faker 'random.uuid' }}",
    "code": {{queryParam 'code'}},
    "name": "{{ faker 'name.firstName' }} {{ faker 'name.lastName' }}",
    "category": {{queryParam 'category' random}}
  },
  {{/repeat}}
  {{else if (urlParam 'category')}}
  {{# repeat (faker 'datatype.number' min=1 max=4) }}
  {
    "id": "{{ faker 'random.uuid' }}",
    "code": {{queryParam 'code'}},
    "name": "{{ faker 'name.firstName' }} {{ faker 'name.lastName' }}",
    "category": {{queryParam 'category' random}}
  },
  {{/repeat}}
  {{else}}
  {{# repeat (faker 'datatype.number' min=7 max=10) }}
  {
    "id": "{{ faker 'random.uuid' }}",
    "code": {{@index}},
    "name": "{{ faker 'name.firstName' }} {{ faker 'name.lastName' }}",
    "category": {{queryParam 'category' random}}
  },
  {{/repeat}}
  {{/if}}
]
